	public void setBuildTarget(String target)
	{
		if(target==null)
			target="";
		buildTarget.setText(target);
	}
	
	public String getMainMethod()
	{
		return mainMethod.getText();
	}
	public String getBuildTarget()
	{
		return buildTarget.getText();
	}

	public void setMainMethod(String method)
	{
		if(method==null || method.length()==0)
			method="Main.main";
		mainMethod.setText(method);
	}

  public String getArgs() {
    return args.getText();
  }

  public void setArgs(String text) {
    args.setText(text);
  }

  public boolean getIsTales() {
    return isTales.isSelected();
  }

  public void setIsTales(boolean on) {
    isTales.setSelected(on);
  }

  public String getJvm() {
    return jvm.getText();
  }

  public void setJvm(String text) {
    jvm.setText(text);
  }
  
  
